var slideIndex = [];
for (var i = 0; i < 100; i++) {
    slideIndex[i] = 1;
}

function openModal(n) {
    document.getElementById("myModal" + n).style.display = "block";
    showSlides(slideIndex[n-1], n);
}

function closeModal(n) {
    document.getElementById("myModal" + n).style.display = "none";
}

function plusSlides(m, n) {
    showSlides(slideIndex[n-1] += m, n);

}

function currentSlide(m, n) {
    showSlides(slideIndex[n-1] = m, n);
}

